import React from "react";
import "./App.css";

type PropsType = {};
type tempType = {
    deviceId: string;
    temp: number;
    logId: string;
    humidity: number;
    tempDate: Date;
    deviceDescription: string;
};
type Device = {
    deviceId: number;
    storeId: number;
    deviceDescription: string;
};
type StatesType = {
    mTemps: tempType[];
    mFilteredList: tempType[];
    deviceList: Device[];
    sort: string | null;
};

export default class App extends React.Component<PropsType, StatesType> {
    static defaultProps: PropsType = {};

    constructor(props: PropsType) {
        super(props);

        this.state = {
            mTemps: [],
            mFilteredList: [],
            deviceList: [],
            sort: null,
        };
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <div
                        className="logo"
                        style={{ fontWeight: "bold", fontSize: "4rem" }}
                    >
                        <span style={{ color: "lightsalmon" }}>Easy</span>{" "}
                        <span style={{ color: "lightblue" }}>Temps</span>
                    </div>{" "}
                    <div
                        className="tempcontainer"
                        style={{
                            backgroundColor: "dimgray",
                            borderRadius: "10px",
                            marginTop: "40px",
                        }}
                    >
                        <div>
                            <label htmlFor="deviceSelector">
                                Select Device ID:
                            </label>
                            <select
                                id="deviceSelector"
                                onChange={(e) => {
                                    const selectedDevice = e.target.value;
                                    this.setState({ sort: selectedDevice });
                                }}
                            >
                                <option value="">All</option>
                                {this.state.deviceList.map((device, index) => (
                                    <option
                                        value={device.deviceId}
                                        key={device.deviceId}
                                    >
                                        {device.deviceId}
                                    </option>
                                ))}
                            </select>
                        </div>

                        {this.state.mFilteredList.map((temp, index) => (
                            <div
                                className="temp"
                                key={temp.logId}
                                style={{
                                    backgroundColor:
                                        index % 2 === 0 ? "lightgray" : "white",
                                    borderRight: "1px solid gray", // Add this line for the border
                                    padding: "5px", // Optional: Add padding for better spacing
                                }}
                            >
                                <p>Device {temp.deviceId}</p>
                                <p>Description {temp.deviceDescription}</p>
                                <p>Temperature {temp.temp}</p>
                                <p>Humidity {temp.humidity}</p>
                            </div>
                        ))}
                    </div>
                </header>
            </div>
        );
    }

    componentDidUpdate(
        prevProps: Readonly<PropsType>,
        prevState: Readonly<StatesType>,
        snapshot?: any
    ): void {
        if (this.state.sort !== prevState.sort && this.state.sort !== null) {
            if (this.state.sort === "") {
                this.setState({ mFilteredList: this.state.mTemps });
            } else {
                const filteredList = this.state.mTemps.filter(
                    (temp) => temp.deviceId.toString() === this.state.sort
                );
                this.setState({ mFilteredList: filteredList });
            }
        }
        if (this.state.sort !== prevState.sort && this.state.sort === null) {
            this.setState({ mFilteredList: this.state.mTemps });
        }
    }

    async componentDidMount(): Promise<void> {
        try {
            const response = await fetch(`http://localhost:3001/temps/`, {
                method: "GET",
            });
            console.log(response); // Log the response
            const temps: tempType[] = await response.json();
            console.log(temps); // Log the prizes
            this.setState({ mTemps: temps, mFilteredList: temps });
        } catch (error) {
            console.error(error);
        }
        try {
            const response = await fetch(`http://localhost:3001/devices/`, {
                method: "GET",
            });
            console.log(response); // Log the response
            const devices: Device[] = await response.json();
            console.log(devices); // Log the prizes
            this.setState({ deviceList: devices });
        } catch (error) {
            console.error(error);
        }
    }
}
